/* Import plugin specific language pack */
 tinyMCE.importPluginLanguagePack('emoticons','en,sv,zh_cn,cs,fa,fr_ca,fr,de');function TinyMCE_emoticons_getControlHTML(control_name){switch(control_name){case "emoticons":return '<img id="{$editor_id}_emoticons" src="{$pluginurl}/images/emoticons.png" title="{$lang_emotions_desc}" width="20" height="20" class="mceButtonNormal" onmouseover="tinyMCE.switchClass(this,\'mceButtonOver\');" onmouseout="tinyMCE.restoreClass(this);" onmousedown="tinyMCE.restoreAndSwitchClass(this,\'mceButtonDown\');" onclick="tinyMCE.execInstanceCommand(\'{$editor_id}\',\'mceEmotion\');">';}return "";}function TinyMCE_emoticons_execCommand(editor_id,element,command,user_interface,value){switch(command){case "mceEmotion":var template=new Array();template['file']='../../plugins/emoticons/emoticons.php';template['width']=150;template['height']=180;tinyMCE.openWindow(template,{editor_id:editor_id});return true;}return false;}